mapper.update:
update admin
set username = #{username,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  phone = #{phone,jdbcType=INTEGER},
  admin_del_state = #{adminDelState,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, username, password, phone, admin_del_state
from admin
where id = #{id,jdbcType=INTEGER}
