mapper.select:
select art.id, art_title, art_author, art_pic, art_time, art_plate_id, pla.pla_name , art_file_id, art_operator_id, art_top, art_del_state, art_content,art_sight from article art LEFT JOIN plate pla on art_plate_id = pla.id where art_del_state = 0
and art_plate_id = #{id,jdbcType=INTEGER}
and art_title like #{search,jdbcType=VARCHAR}
order by art_top desc, art_time desc

